sourcesContent:
import express from 'express';
import path from 'path';
import cors from 'cors';
import bodyParser from 'body-parser';

// Import all the controllers
import AppController from '../controllers/AppController.js';
import UsersController from '../controllers/UsersController.js';
import AuthController from '../controllers/AuthController.js';
import FilesController from '../controllers/FilesController.js';

// Import the middlewares
import { basicAuthenticate, xTokenAuthenticate } from '../middlewares/auth.js';
import { APIError, errorResponse } from '../middlewares/error.js';
import i18nMiddleware from '../middlewares/i18n.js';

// Create Express Router
const createRouter = () => {
  const router = express.Router();

  // Apply middlewares
  router.use(cors());
  router.use(bodyParser.json());
  router.use(bodyParser.urlencoded({ extended: true }));
  router.use(i18nMiddleware);

  // Home route with translatable welcome message
  router.get('/', (req, res) => {
    res.json({
      message: req.t('welcome')
    });
  });

  // Status and stats routes
  router.get('/status', AppController.getStatus);
  router.get('/stats', AppController.getStats);

  // User routes
  router.post('/users', UsersController.postNew);
  router.get('/users/me', xTokenAuthenticate, UsersController.getMe);
  router.delete('/users/me', xTokenAuthenticate, UsersController.deleteMe);
  router.get('/users', xTokenAuthenticate, UsersController.getIndex);
  router.get('/users/:id', xTokenAuthenticate, UsersController.getShow);

  // Authentication routes
  router.get('/connect', basicAuthenticate, AuthController.getConnect);
  router.get('/disconnect', xTokenAuthenticate, AuthController.getDisconnect);

  // File management routes
  router.post('/files', xTokenAuthenticate, FilesController.postUpload);
  router.get('/files/:id', xTokenAuthenticate, FilesController.getShow);
  router.get('/files', xTokenAuthenticate, FilesController.getIndex);
  router.put('/files/:id/publish', xTokenAuthenticate, FilesController.putPublish);
  router.put('/files/:id/unpublish', xTokenAuthenticate, FilesController.putUnpublish);
  router.get('/files/:id/data', FilesController.getFile);
  router.delete('/files/:id', xTokenAuthenticate, FilesController.deleteFile);

  // 404 handler for undefined routes
  router.all('*', (req, res, next) => {
    errorResponse(
      new APIError(404, `Cannot ${req.method} ${req.url}`), 
      req, 
      res, 
      next
    );
  });

  // Error handling middleware
  router.use(errorResponse);

  return router;
};

// Main application setup
const setupApp = (app) => {
  // Middleware for parsing JSON and handling CORS
  app.use(cors());
  app.use(bodyParser.json());
  app.use(bodyParser.urlencoded({ extended: true }));

  // Serve static files from 'public' directory
  app.use(express.static(path.join(process.cwd(), 'public')));

  // Create and use the router
  const router = createRouter();
  app.use('/api', router);

  // Fallback route to serve index.html for client-side routing
  app.get('*', (req, res) => {
    res.sendFile(path.join(process.cwd(), 'public', 'index.html'));
  });

  return app;
};

export { createRouter, setupApp };

